    memset(frt,0x7f,sizeof(frt));
    memset(mi,0x7f,sizeof(mi));
    for(int i=1;i<=n;i++)
        a[i].w+=a[i-1].w,frt[a[i].w+n+1]=min(frt[a[i].w+n+1],a[i].x);
    for(int i=1;i<=2*n+1;i++)
    {
        mi[i]=frt[i];
        if(i>=3) mi[i]=min(mi[i],mi[i-2]);
    }
    for(int i=1;i<=n;i++) ans=max(ans,a[i].x-mi[a[i].w+n]);
    printf("%d",ans);
    return 0;
}
