{
	read(n);read(m);
	N=(1<<n)-1;
	for(int i=1;i<=n;i++){
		zy[i]=(1<<(i-1));
		read(h[i]);read(w[i]);read(s[i]);
	}
	for(int i=0;i<=N;i++){
		f[i]=-1e9;
		for(int j=1;j<=n;j++){
			if(i&zy[j])H[i]+=h[j];//预处理状态高度和 
		}
	}
	for(int i=1;i<=n;i++){
		f[zy[i]]=s[i];//预处理，先放一头 
	}
	for(int i=1;i<=N;i++){
		for(int j=1;j<=n;j++){
			if(!(i&zy[j])){
				f[i|zy[j]]=max(f[i|zy[j]],min(f[i]-w[j],s[j]));
			}
		}
	}
	int ans=-1;
	for(int i=1;i<=N;i++){
		if(H[i]>=m&&f[i]>=0){
