#include<cstdio>
#include<algorithm>
#define int long long
using namespace std;
const int maxn=21;
int n,m,h[maxn],w[maxn],s[maxn];
inline void init(){
	scanf("%lld%lld",&n,&m);
	for (int i=1;i<=n;i++){
		scanf("%lld%lld%lld",&h[i],&w[i],&s[i]);
	}
}
const int inf=1e18;
int dp[1<<maxn],ans;
inline void solve(){
	ans=-inf; dp[0]=inf;
	for (int S=1;S<(1<<n);S++){
		dp[S]=-inf; int now=0;
		for (int i=1;i<=n;i++){
			if (S&(1<<(i-1))){
				dp[S]=max(dp[S],min(dp[S^(1<<(i-1))]-w[i],s[i]));
				now+=h[i];
			}
		}
		if (now>=m&&dp[S]>=0) ans=max(dp[S],ans);
	}
	if (ans<0) puts("Mark is too tall"); else printf("%lld\n",ans);
}
signed main(){
	init();
	solve();
	return 0;
} 
