const maxi=1 shl 30;
var
 f,hh:array[0..ma] of longint;
 h,w,s:array[1..20] of longint;
 i,n,hi,j,k,a,l,ans:longint;
function max(x,y:longint):longint;
begin
 if x>y then exit(x)
  else exit(y)
end;
function min(x,y:longint):longint;
begin
 if x>y then exit(y);
 exit(x)
end;
begin
 f[0]:=maxi;
 readln(n,hi);
 for i:=1 to n do
 begin
  readln(h[i],w[i],s[i]);
  f[1 shl (i-1)]:=s[i];
  hh[1 shl (i-1)]:=h[i]
 end;
 ans:=-1;
 for i:=1 to 1 shl n-1 do
 begin
  if i=4 then
  begin
   j:=j
  end;
  j:=i;
  k:=0;
  a:=-1;
  while j>0 do
  begin
   inc(k);
   if j xor (j shr 1 shl 1)=1 then
   begin
    l:=i xor (1 shl (k-1));
    hh[i]:=hh[l]+h[k];
if w[k]<=f[l] then a:=max(a,min(f[l]-w[k],s[k]))
end;
   j:=j shr 1
  end;
  f[i]:=a;
  if hh[i]>=hi then
   if a>ans then
