		cin>>t;
		f[t][i]=i+1;
	}
	for (int i=2; i<=58; i++) //202144=2^18 题目明确最大范围是40 40+18=58
		for (int j=1; j<=n; j++) {
			if (!f[i][j])
				f[i][j]=f[i-1][f[i-1][j]];
			if (f[i][j])
				ans=i;
		}
	cout<<ans;
