//for(int i=1;i<=n;++i)
 //printf("i:%d maxx1:%lld minn1:%lld maxx2:%lld minn2:%lld\n",i,maxx1[i],minn1[i],maxx2[i],minn2[i]);
    ans=(maxx2[1]-minn2[1])*(point[n].x-point[1].x);
//cout<<"ans:----"<<ans<<endl;
