     ans=min(ans,(point[i].y-point[1].y)*(maxx1[i]-minn1[i])+(point[n].y-point[i+1].y)*(maxx2[i+1]-minn2[i+1]));  
 //   cout<<(maxx1[n]-minn1[n])*(point[n].y-point[1].y)<<endl;  
