#endif
	int n=gi(),m=gi(),N=n*m;
	for(rg int i=1;i<=n;++i)
		for(rg int j=1;j<=m;++j)
			id[i][j]=++id[0][0];
	for(rg int i=1;i<=n;++i)
		for(rg int j=1;j<=m;++j)
			S[id[i][j]][id[i][j]]=1;
	for(rg int i=1;i<=n;++i)
		for(rg int j=1;j<m;++j)
			S[id[i][j]][id[i][j+1]]=1;
	for(rg int i=1;i<=n;++i)
		for(rg int j=2;j<=m;++j)
			S[id[i][j]][id[i][j-1]]=1;
	for(rg int i=1;i<n;++i)
		for(rg int j=1;j<=m;++j)
			S[id[i][j]][id[i+1][j]]=1;
	for(rg int i=2;i<=n;++i)
