```cpp
#include <bits/stdc++.h>

using namespace std;

typedef long long ll;

ll n, m, ans;

ll gcd(ll a,ll b)
{
	if (b == 0)
		return a;
	return gcd(b, a % b);
}
int main()
{
	scanf("%lld%lld", &m ,&n);
	n ++ ;
	m ++ ;
	ans = (n * m) * (n * m - 1) * (n * m - 2) / (1 * 2 * 3);
	ans -= n * m * (m - 1) * (m - 2) / (1 * 2 * 3);
	ans -= m * n * (n - 1) * (n - 2) / (1 * 2 * 3);
	for (ll i = 2; i < n; i ++ )
		for (ll j = 2; j < m; j ++ )
		{
			ans -= (n - i) * (m - j) * (gcd(i, j) - 1) * 2;
		}
	printf("%lld\n", ans);
	return 0;
}
