struct node{
	int x,y;
}a[100001];
bool cmp(node a1,node b1){
    return a1.x>b1.x;
}
int main(){
	int n,m,x=0,y=0,ans=0;
    cin>>n>>m;
    for(int i=1;i<n;i++){
    	cin>>a[i].x;
        a[i].y=1;
    }
    for(int i=n;i<=n+m-2;i++){
        cin>>a[i].x;
        a[i].y=2;
    }
    sort(a+1,a+n+m-1,cmp);
    for(int i=1;i<=n+m-2;i++){
    	if(a[i].y==1){
            x++;
            ans+=a[i].x*(y+1);
        }
        else{
            y++;
            ans+=a[i].x*(x+1);
        }
