#define INF 0x7fffffff
#define inf 0x3f
#define int long long
#define ll long long
#define lson (now<<1)
#define rson (now<<1|1)
#define pii pair<int ,int >
#define pushup1(now)  tr[now].sum=(tr[lson].sum+tr[rson].sum);
#define pushup2(now)  tr[now].sq=(tr[lson].sq+tr[rson].sq);
using namespace std;
const int mod=1e9+7;
inline int read() {
	int now=0;
	char c=getchar();
