    for( re int i = 0; i < n; i += ( k << 1 ) )
    for( re int j = i; j < i + k; ++ j ) {
        double Nx = p[j], Ny = p[j + k] ; 
        p[j + k] = ( Nx + Ny ), p[j] = Nx ;
