        for(int i=0;i<max(e.m,m);++i) {
            w.a[i]+=a[i]+e.a[i];
            w.a[i+1]+=w.a[i]/10,w.a[i]%=10;
        }
        w.m=max(e.m,m)+(w.a[max(e.m,m)]>0);
