	return a;
}//高精加
high mul(high a, int b)
{
	for(int i = 1; i <= a.len; ++ i) a.a[i] *= b;
	a.len += 4;
	for(int i = 1; i <= a.len; ++ i)
	{
		if(a.a[i] > 9) 
			a.a[i + 1] += a.a[i] / 10, a.a[i] %= 10;
	}
	while(a.a[a.len] == 0) a.len --;//处理前导零
	return a;
}//高精乘低精
void write(high a)
{
