}c[200001];
int n,m,num,ans;
int h[100001],ru[100001],cu[100001];
int f[100001];
ini read()
{
    int x=0;
    char ch=getchar();
    while(!is)
    ch=getchar();
    while(is)
    {
        x=x*10+ch-48;
