    }
    return x;
}
inv add(int x,int y)
{
    c[++num].x=h[x];
    c[num].y=y;
    h[x]=num;
}
inv dfs(int x,int y)
{
    if(f[x]) return;
    if(!cu[x])
    {
        f[x]=1;
        return;
    }
    for(int i=h[x];i;i=c[i].x)
      if(c[i].y!=y)
      {
          dfs(c[i].y,x);
          f[x]+=f[c[i].y];
      }
}
int main()
{
    n=read(),m=read();
    for(int i=1;i<=m;i++)
      {
          int x=read(),y=read();
          ru[y]++;
          cu[x]++;
          add(x,y);
      }
    for(int i=1;i<=n;i++)
      if(!ru[i]&&cu[i])
