}a[Max];
int head[Max >> 1],sum = 0;
void add(int x,int y)
{
	a[++ sum].y = y;
	a[sum].ne = head[x];
	head[x] = sum;
}
int ru[Max >> 1],chu[Max >> 1];
int dp[Max >> 1];
int ans = 0;

int dfs(int x)
{
	if(dp[x] != 0)return dp[x];
	int ans = 0;
	if(ru[x] != 0 && chu[x] == 0)
		ans ++;
	for(register int i = head[x];i != 0;i = a[i].ne)
	{
		ans += dfs(a[i].y);
	}
	dp[x] = ans;
	return ans;
}

signed main()
{
	int n,m;
	cin >> n >> m;
	int a,b;
	for(register int i = 1;i <= m;++ i)
	{
		cin >> a >> b;
		add(a,b);
		chu[a] ++;
		ru[b] ++;
	}
	int tot = 0;
	for(register int i = 1;i <= n;++ i)
		if(ru[i] == 0 && chu[i] != 0)
			tot += dfs(i);
	cout << tot << endl;
	return 0; 
}
