}node[100001];
struct k2{
    int from;
    int to;
    int next_;
}edge[200001];
void edge_add(int x,int y)
{
    num++;
    edge[num].from=x;
    edge[num].to=y;
    edge[num].next_=node[x].first_;
    node[x].first_=num;
    node[x].chu_du++;
    node[y].ru_du++;
}
int main()
{
    scanf("%d%d",&n,&m);
    for(int i=1;i<=m;i++)
    {
        scanf("%d%d",&f1,&f2);
        edge_add(f1,f2);
    }
    head=0;
    tail=0;
    for(int i=1;i<=n;i++)
    {
        if((node[i].ru_du==0)&&(node[i].chu_du>0))
        {
            tail++;
            node[i].ans=1;
            t[tail]=i;
        }
    }
    while(head<tail)
    {
        head++;
        p=t[head];
        for(int j=node[p].first_;j!=0;j=edge[j].next_)
        {
            q=edge[j].to;
            node[q].ans+=node[p].ans;
            node[q].ru_du--;
            if(node[q].ru_du==0)
            {
                tail++;
                t[tail]=q;
            }
        }
    }
    for(int i=1;i<=n;i++)
    {
        if(node[i].chu_du==0)
            ans+=node[i].ans;
    }
    printf("%d",ans);
    return 0;
}
