```cpp
#include<iostream>
#include<cstdlib>
#include<cstdio>
#include<cmath>
#include<algorithm>
#include<cstring>
#include<queue>
#include<vector>
#include<stack>
#define MAXN 1000010
#define RG register
#define LL long long int
using namespace std;
const int INF=1e9;
const int mod=31011;
struct node{
  int next;
  int to;
}t[MAXN*2];
int head[MAXN*2],num;
int n,m;
int ans;
int du[MAXN],vis[MAXN],q[MAXN];
int col[MAXN],used[MAXN];
void add(int from,int to)
{
  t[++num].next=head[from];
  t[num].to=to;
  head[from]=num;
}
int main()
{
  freopen("1.in","r",stdin);
  scanf("%d%d",&n,&m);int x,y;
  for(int i=1;i<=m;i++){
    scanf("%d%d",&x,&y);add(x,y);add(y,x);
  }
  for(int i=n;i>=1;i--){
    int p=0;
    for(int j=1;j<=n;j++) if(!vis[j] && du[j]>=du[p]) p=j;
    q[i]=p;vis[p]=1;
    for(int j=head[p];j;j=t[j].next) du[t[j].to]++;
  }
  for(int i=n;i>=1;i--){
    int p=q[i];
    for(int j=head[p];j;j=t[j].next) used[col[t[j].to]]=p;
    int j;
    for(j=1;;j++) if(used[j]!=p) break;
    col[p]=j;if(j>ans) ans=j;
  }
  printf("%d\n",ans);
  return 0;
}

