	scanf("%lld%lld",&m,&n);
	ans=(pow(m,n,MOD)-((m%MOD)*pow(m-1,n-1,MOD))%MOD)%MOD;
	if(ans<0)//如果结果是个负数，一定要转化为非负整数
		ans+=MOD;
	printf("%lld",ans%MOD);//输出
