inline ll fac(ll x,ll pi,ll pk) //求阶乘
{
    if (!x) return 1;
    ll ans=1;
    for (ll i=2;i<=pk;i++)
    {
        if (i%pi==0) continue;
        ans=ans*i%pk;
    }
    ans=qpow(ans,x/pk,pk);
    ll len=x%pk;
    for (ll i=2;i<=len;i++)
