}

inline BigInt A(int n,int m){
  BigInt ans,x;ans.clear();ans.give(1);
  if(!m)return ans;
  if(m>n){ans.clear();return ans;}
  for(LL i=n-m+1;i<=n;++i){x.give(i);ans=ans*x;}
