#define mod 100000000
#define ll long long
using namespace std;
struct NUM
{
    vector<ll>val;    
    NUM(){val.clear();val.resize(1,0);}
    void clear(){while(!val.back()&&val.size()>1) val.pop_back();}
    NUM operator+(NUM b)
    {
        NUM a=*this,c;
        int len1=a.val.size();
        int len2=b.val.size();
        int len3=max(len1,len2)+1;
        c.val.resize(len3,0);
        for(int i=0;i<len3;++i){
            if(i<len1) c.val[i]+=a.val[i];
            if(i<len2) c.val[i]+=b.val[i];
            if(i<len3-1){
                c.val[i+1]+=c.val[i]/mod;
                c.val[i]%=mod;
            }
        }
        c.clear();
        return c;
    }
    NUM operator*(const int x){
        NUM a=*this;
        int ret=0;
        for(int i=0;i<a.val.size();++i){
            a.val[i]*=x;
            a.val[i]+=ret;
            ret=a.val[i]/mod;
            a.val[i]%=mod;
        }
        a.val.push_back(ret);
        a.clear();
        return a;
    }
    NUM operator*(NUM b)
    {
        NUM a=*this,c;
        int len1=a.val.size();
        int len2=b.val.size();
        int len3=len1+len2;
        c.val.resize(len3,0);
        for(int i=0;i<len1;++i)
            for(int j=0;j<len2;++j)
                c.val[i+j]+=a.val[i]*b.val[j];
        for(int i=0;i<len3-1;++i){
            c.val[i+1]+=c.val[i]/mod;
            c.val[i]%=mod;
        }
        c.clear();
        return c;
    }
    NUM operator/(const int x){
        NUM a=*this,b;
        int len=a.val.size();
        b.val.resize(len,0);
        for(int i=len-1;i>=1;--i){
            b.val[i]=a.val[i]/x;
            a.val[i-1]+=a.val[i]%x*mod;
        }
        b.val[0]=a.val[0]/x;
        b.clear();
        return b;
    }
    NUM operator*=(const int x){return *this=*this*x;}
    NUM operator/=(const int x){return *this=*this/x;}
