#include <bits/stdc++.h>
#define ull unsigned long long
#define ll int
#define mod 1000000007
using namespace std;
ll sc[15],a[15];
ll n,scc[15]={ };
ll cnt1,cnt2;
map<ull,ll> Map;
ull Hash(int x){
	for(int i=x+1;i<=n;i++) a[i]=sc[i]-scc[i];
	stable_sort(a+x+1,a+n+1);
    ull tot=0;
    for(int i=x+1;i<=n;i++) tot=tot*131+a[i];
    return tot;
}
ll dfs(int i,int j){
	if(i==n+1) return 1;
	if(scc[i]>sc[i]) return 0;
	if(sc[i]-scc[i]>(n-j+1)*3) return 0; 
	if(j==n+1&&scc[i]<sc[i]) return 0;
	if(j==n+1&&scc[i]==sc[i]) {
		ull pos=Hash(i);
		if(Map.count(pos)) return Map[pos];
		else{
			ll sop=dfs(i+1,i+2);
			return Map[pos]=sop;
		}
	}
	ll pos=0;
	if(cnt1){
	    cnt1--;
    	scc[i]+=3;
    	pos+=dfs(i,j+1);
    	scc[i]-=3,scc[j]+=3;
    	pos+=dfs(i,j+1);
		scc[j]-=3,cnt1++;
	}
	if(cnt2){
	    cnt2--;
    	scc[i]+=1,scc[j]+=1;
    	pos+=dfs(i,j+1);
    	scc[i]-=1,scc[j]-=1;
    	cnt2++;
	}
	return pos%mod;
}
bool cmp(int x,int y){
	return x>y;
}
int main() {
	cin >> n;
	ll pp=0;
	for(int i=1;i<=n;++i){
		cin >> sc[i];
		pp+=sc[i];
	}
	sort(sc+1,sc+n+1,cmp);
	cnt1=pp-n*n+n;
	cnt2=(pp-3*cnt1)/2;
	cout << dfs(1,2)%mod;
	return 0;
}
