#include<bits/stdc++.h>
#define ll long long
const int N=2e5+10;
using namespace std;

struct node
{
    ll l,r,id;
}h[N];
struct date
{
    ll d,id;
}s[N];
ll n,m,size,p;
ll a[N],num[N],bl[N],cnt[N],now,ans[N];
char c[N];

ll read()
{
    ll a,f=1;
    char c;
    c=getchar();
    while(!isdigit(c)){f=c=='-'?-1:f; c=getchar();}
    a=c-'0'; c=getchar();
    while(isdigit(c)){a=a*10+c-'0'; c=getchar();}
    return a*f;
}

void solve()
{
    ll i,l,r,tot,mus;
    for(i=1;i<=n;i++)
    {
      s[i]=s[i-1];
      if(a[i]%p==0){s[i].d+=i; s[i].id++;}
    }
    m=read();
    while(m--)
    {
      l=read(); r=read();
      tot=s[r].d-s[l-1].d;//全部的贡献 
      mus=(l-1)*(s[r].id-s[l-1].id);//区间[1,l-1]里产生的贡献 
      printf("%lld\n",tot-mus);//相减得到[l,r]里的贡献 
    }
}

bool cmp1(date x,date y){return x.d<y.d;}

void prework()
{
    ll i,len=0,pow=1;
    size=sqrt(n);//莫队分块的时候，不能在n++之后分，不然会出问题emmm 
	bl[n+1]=n/size+1;
    for(i=n;i>0;i--)//算出[i,n]这段数字%p的余数 
    {
      s[i].d=(s[i+1].d+a[i]*pow)%p;
      s[i].id=i; pow=pow*10%p;//s[i].id用来记录原来的编号 
      bl[i]=(i-1)/size+1;//顺便分块 
    }
    s[++n].d=0; s[n].id=n;//新加一个点 
    sort(s+1,s+n+1,cmp1);
    for(i=1;i<=n;i++)//（非常朴素的）离散化 
    {
      if(i==1||s[i].d!=s[i-1].d) len++;
      num[s[i].id]=len;
    }
}

bool cmp2(node x,node y)
{
    if(bl[x.l]^bl[y.l]) return bl[x.l]<bl[y.l];
    return (bl[x.l]&1)?(x.r<y.r):(x.r>y.r);
	//莫队排序小技巧： 奇数块升序，偶数块降序 
}

void add(ll x){now+=cnt[num[x]]; cnt[num[x]]++;}
//注意now与cnt数组改变的先后顺序 
void del(ll x){cnt[num[x]]--; now-=cnt[num[x]];}

int main()
{
    ll i,j,l,r;
    p=read(); scanf("%s",c);
    n=strlen(c);
	for(i=1;i<=n;i++) a[i]=c[i-1]-'0';
    if(p==2||p==5){solve(); return 0;}//特判 
    prework();
    m=read();
    for(i=1;i<=m;i++)
    {
      h[i].l=read();
      h[i].r=read()+1;//在这里就+1 
      h[i].id=i;
    }
    sort(h+1,h+m+1,cmp2);
    l=1; r=0;
    for(i=1;i<=m;i++)//莫队 
    {
      while(l<h[i].l) del(l++);
      while(l>h[i].l) add(--l);
      while(r<h[i].r) add(++r);
      while(r>h[i].r) del(r--);
      ans[h[i].id]=now;
    }
    for(i=1;i<=m;i++) printf("%lld\n",ans[i]);
    return 0;
}
