	swap (f, s);
	swap (df, ds);
	swap (l1, l2);
	swap (r1, r2);
	fl |= judge (f, s, df, ds, ceil ((l1 - f) * 1.0 / df), l1, r1, l2, r2) | judge (f, s, df, ds, floor ((r1 - f) * 1.0 / df), l1, r1, l2, r2);
	return fl;
}
bool check_three (LL Limit, LL x) {
