#define N 7
#define ll long long
using namespace std;
int T;
ll ans,k,m,x,k1,m1,x1;
ll a[N],b[N],c[N],d[N];
bool Check1()
{
    ll kx=k;
    if(x-k*2<=k*3+k*2&&x-k*2!=k*3+k*2-1)
        return 1;
    while((x-kx*2)%3!=0)
        kx--;
    if((x-kx*2)<=k*6)
        return 1;
    return 0;
}
bool Check2(ll now)
{
    if(now<2)
        return 0;
    if(now<=k*3+k*2&&now!=k*3+k*2-1)
        return 1;
    return 0;
}
bool Check3(ll now)
{
    if(now>=0&&now%2==0&&now/2<=k)
        return 1;
    return 0;
}
bool Check4(ll now)
{
    if(now<0)
        return 0;
    ll kx=k;
    if(now<=k*3+k*2&&now!=k*3+k*2-1)
        return 1;
    if(now%3==0&&now<=k*6)
        return 1;
    return 0;
}
bool Check5(ll now)
{
    if(now<0)
        return 0;
    if(now%3==0&&now/3<=k)
        return 1;
    if(now%2==0&&now/2<=k)
        return 1;
    if(now<=k)
        return 1;
    return 0;
}
int main()
{
    scanf("%d",&T);
    scanf("%lld%lld%lld%lld%lld",&a[1],&b[1],&c[1],&d[1],&k);
