#include <cstdio>
#include <iostream>
#include <cstring>
#include <cmath>
#include <algorithm>
#include <queue>
#include <vector>

typedef long long ll;
const int K = 21;
const int N = 52;
const int V = 250;

char buf[1 << 25] ,*p1 = buf ,*p2 = buf;
#define getchar() (p1 == p2 && (p2 = (p1 = buf) + fread(buf ,1 ,1 << 21 ,stdin) ,p1 == p2) ? EOF : *p1++)
inline ll read() {
    ll x = 0, f = 1; char ch = getchar();
    while(ch > '9' || ch < '0') { if(ch == '-') f = -1; ch = getchar(); }
    while(ch >= '0' && ch <= '9') x = x * 10 + ch - 48, ch = getchar();
    return x * f;
}

int k,b[N],t; ll f[N][2],g[N][2],dp[N][V][V][2];

void Div(ll n) {
    if(!n) return;
    b[++t] = n % k, Div(n / k);
    return;
}

ll F(ll n) {
    t = 0, Div(n);
    memset(f,0,sizeof(f)), memset(g,0,sizeof(g));
    f[t][1] = 1, g[t][1] = 0;
    f[t][0] = b[t], g[t][0] = 0;
    for(int i = t;i > 1;i--) {
        for(int p = 0;p < 2;p++) for(int v = 0;v < k;v++) {
            if(p && v > b[i - 1]) continue;
            int q = (p && v == b[i - 1]) ? 1 : 0;
            f[i - 1][q] += f[i][p];
            g[i - 1][q] += g[i][p] + 1ll * v * (t - i + 1) * f[i][p];
        }
    }
    ll ans = g[1][0] + g[1][1];
    //std::printf("ans = %lld\n",ans);
    for(int P = t;P > 1;P--) {
        memset(dp,0,sizeof(dp));
        dp[t + 1][0][0][1] = 1;
        for(int i = t + 1;i > 1;i--) {
            for(int x = 0;x <= (k - 1) * (t - i + 1);x++) 
                for(int y = 0;x + y <= (k - 1) * (t - i + 1);y++)
                    for(int p = 0;p < 2;p++) for(int v = 0;v < k;v++) {
                        if(p && v > b[i - 1]) continue;
                        int q = (p && v == b[i - 1]) ? 1 : 0;
                        int _x = x, _y = y;
                        (i - 1 >= P ? _x : _y) += v;
                        if(_x <= V && _y <= V)
                            dp[i - 1][_x][_y][q] += dp[i][x][y][p];
                    }
        }
        for(int x = 0;x <= (k - 1) * t;x++) 
            for(int y = x;x + y <= (k - 1) * t;y++)
                ans += 1ll * (x - y) * (dp[1][x][y][0] + dp[1][x][y][1]);
    }
    //std::printf("lstans = %lld\n",ans);
    return ans;
}

int main() {
    ll l = read(), r = read();
    k = read(); 
    std::printf("%lld\n",F(r) - F(l - 1));
    return 0;
}

