```cpp
#include<bits/stdc++.h>
#define lowbit(x) (x & -x)
#define rep(i,x,y) for(register int i = x ;i <= y;++i)
#define repd(i,x,y) for(register int i = x ;i >= y;--i)
using namespace std;
typedef long long ll;
template<typename T>inline void read(T&x)
{
    x = 0;char c;int sign = 1;
    do { c = getchar(); if(c == '-') sign = -1; }while(c < '0' || c > '9' );
    do { x = x * 10 + c - '0'; c = getchar(); }while(c <= '9' && c >= '0');
    x *= sign;
}
const int N = 1e4 + 600, K = 521;
int a[N],ans,mx,n,k;
int c[N][K];
inline void modify(int x,int y,int z)
{
    for(register int i = x;i <= mx + k; i += lowbit(i))
        for(register int j = y;j <= k + 1; j += lowbit(j))
            c[i][j] = max(c[i][j],z);
}
inline int query(int x,int y)
{
    int ans = 0;
    for(register int i = x;i; i -= lowbit(i))
        for(register int j = y;j; j -= lowbit(j))
            ans = max(ans,c[i][j]);
    return ans;
}
int main()
{
    read(n);read(k);
    rep(i,1,n) read(a[i]),mx = max(mx,a[i]);
    int x;
    rep(i,1,n)
        repd(j,k,0)
        {
            x = query(a[i] + j,j + 1) + 1;
            ans = max(ans,x);
            modify(a[i] + j,j + 1,x);
        }
    cout<<ans<<endl;
