    while(c>='0'&&c<='9')    ret=ret*10+c-'0',c=getchar();
    return ret;
}
int find(int x){
    return fa[x]==x ? x : fa[x] = find(fa[x]);
}
void merge(int l1,int l2,int k){
    int f1 = find(ST[l1][k]);    int f2 = find(ST[l2][k]);
    if(f1>f2)    swap(f1,f2);     
    fa[f2] = f1;
}
int main(){
