		a = a * a % mod;
		b >>= 1;
	}
	return ret;
}
void exgcd(int a, int b, int &x, int &y) {
	if (!b) x = 1, y = 0;
	else exgcd(b, a % b, y, x), y -= a / b * x;
}
int inv(int a, int p) {
