long long NUMOFCASES, a, b, p, x, t, X, Y;

long long exp(long long b, int i, int p)
{
    long long r = 1;
    for (; i; i >>= 1, b = b * b % p)
        if (i & 1)
            r = r * b % p;
    return r;
