        }
        else if (a == 0)
        {
            if (b == t)
                cout << 2 << endl;
            else
                cout << -1 << endl;
        }
        else
        {
            long long ans = bsgs(a, ((t - b * inv(1 - a, p)) % p + p) % p * inv(((x - b * inv(1 - a, p)) % p + p) % p, p), p) % p;
            if (ans == -1)
