#include<bits/stdc++.h>
#define ll long long 
#define mul(x,y) (((x%p+p)%p)*((y%p+p)%p))%p

using namespace std;

int T;
ll p,a,b,X_1,t,x,y;
map <ll,int> vh;

ll quickp(ll x,ll y) {
	if(!y) return 1;
	ll t=quickp(x,y/2);
	if(y&1) return mul(x,mul(t,t));
	return mul(t,t);
}

ll EXGCD(ll a,ll b,ll &x,ll &y) {
	if(!b) {
		x=1;
		y=0;
		return a;
	}
	ll gcd=EXGCD(b,a%b,x,y),t=x;
	x=y;
	y=t-a/b*y;
	return gcd;
}

ll BSGS(ll a,ll b,ll p) {
	vh.clear();
	if(b==1) return 0;
	if(!(a%p)) {
		if(!b) return 1;
		return -2;
	}
	b%=p;
	ll k=ceil(sqrt(p)),tmp=b,t=quickp(a,k);
	for(int j=1;j<=k;j++) {
		tmp=mul(tmp,a);
		vh[tmp]=j;
	}
	tmp=1;
	for(int i=1;i<=k;i++) {
		tmp=mul(tmp,t);
		if(vh[tmp]) return ((i*k-vh[tmp])%p+p)%p;
	}
	return -2;
}

int main() {
	cin>>T;

	while(T--) {
		scanf("%lld%lld%lld%lld%lld",&p,&a,&b,&X_1,&t);
		if(X_1==t) printf("1\n");
		else if(!a) {
			if(b==t) printf("2\n");
			else printf("-1\n");
		}
		else if(a==1) {
			ll Y=((t+b-X_1)%p+p)%p,gcd=EXGCD(b,p,x,y),ans;
			if(Y%gcd) printf("-1\n");
			else {
				x*=Y/gcd;
				ans=(x%p+p)%p;
				printf("%lld\n",!ans?p:ans);
			}
		}
		else {
			ll A=a,B=mul(mul(a,t)-t+b,quickp(mul(mul(a,X_1)-X_1+b,1),p-2));
			ll ans=BSGS(A,B,p);
			printf("%lld\n",ans+1);
		}
	}

	return 0;
}
