		for(int l = 1, r; l <= a; l = r + 1) {
			r = (a / (a / l));
			int sm = Dec(mu[r] + mod, mu[l - 1] + mod);
			sm = Mul(sm, sq3(a / l));
			ans = Add(ans, sm);
		}
		return ans;
	}
	int calc2(int a) {
		int ans = 0;
		for(int l = 1, r; l <= a; l = r + 1) {
			r = (a / (a / l));
			int sm = Dec(mu[r] + mod, mu[l - 1] + mod);
