	void Exgcd(int a, int b, int &x, int &y) {
		if(!b) {
			x = 1;
			y = 0;
			return ;
		}
		Exgcd(b, a % b, x, y);
		int t = x;
		x = y;
		y = t - a / b * y;
	}

	void solve() {
		shai();
		int T, n, a;
