```cpp
#include <stdio.h>
#include <string.h>
#define MXN    50001
#define min(a,b) (a>b?b:a)
typedef long long _L;
int Ads[MXN];
char Mue[MXN];
int MSm[MXN];
bool Npr[MXN];
int Prm[MXN];
int Prt;
void LoadMue()
{
    Mue[1]=MSm[1]=1;
    int wi,wib;
    for(wi=2;wi<MXN;++wi)
    {
        if(!Npr[wi])
        {
            Mue[wi]=-1;
            Prm[++Prt]=wi;
        }
        for(wib=1;Prm[wib]*wi<MXN;++wib)
        {
            Npr[Prm[wib]*wi]=1;
            if(wi%Prm[wib]==0)
            {
                Mue[wi*Prm[wib]]=0;
                break;
            }
            Mue[wi*Prm[wib]]=-Mue[wi];
        }
        MSm[wi]=MSm[wi-1]+Mue[wi];
    }
}
void LoadAds()
{
    int wi,wia,last;
    for(wi=1;wi<MXN;++wi)
        for(wia=1;wia<=wi;wia=last+1)
        {
            last=wi/(wi/wia);
            Ads[wi]+=(wi/wia)*(last-wia+1);
        }
}
_L TryDiv(int N,int M)
{
    _L ret=0,buf;
    if(N>M)
        N^=M^=N^=M;
    int wi,last;
    for(wi=1;wi<=N;wi=last+1)
    {
        last=min(N/(N/wi),M/(M/wi));
        buf=Ads[N/wi];
        buf=buf*Ads[M/wi];
        ret+=buf*(MSm[last]-MSm[wi-1]);
    }
    return ret;
}
int T,N,M;
void IA()
{
    scanf("%d",&T);
    LoadMue();
    LoadAds();
    while(T--)
    {
        scanf("%d %d",&N,&M);
        printf("%lld\n",TryDiv(N,M));
    }
}
int main()
{
    IA();
    return 0;
}

