inline void Work () {
	scanf ( "%d %d %d", &P, &Q, &R );
	for ( Int y = 1; y <= Q; ++ y ) {
		for ( Int x = 1; x <= P; ++ x ) {
			for ( Int z = 1; z <= R; ++ z ) {
				char s = getchar ();
				for ( ; s ^ 'P' && s ^ 'N'; s = getchar () );
				W[x][y][z] = s == 'N';
