    2.树状数组版

    ```cpp
	#include<bits/stdc++.h>
	using namespace std;
	#define maxn 100010
	#define max_(x,y) ((x)>(y)?(x):(y))
	#define min_(x,y) ((x)<(y)?(x):(y))
	#define dd double
	#define ull unsigned long long
	#define ll long long
	#define lowbit(x) ((x)&(-x))
	int c[maxn];
	int n,m,x,b,far,ans;
	inline void upd(int x,int v){
		while(x<=n){
			c[x]+=v;
			x+=lowbit(x);
		}
	}
	inline int sum(int x){
		int sum=0;
		while(x>0){
			sum+=c[x];
			x-=lowbit(x);
		}
		return sum;
	}
	inline void NMSP(){
		cin>>n>>m;
		for(register int i=1;i<=n;i++){
			cin>>x>>b;
			upd(x,b);
			far=max_(x,far);
		}
		for(register int i=m;i<=far;i++)
			ans=max_(ans,sum(i)-sum(i-m));
		cout<<ans;
	}	
	signed main(){
		ios::sync_with_stdio(0);
		cin.tie(0);
		NMSP();
		return 0;
	}
	```
