    k1=rank(k1);
    k2=rank(k2);
    for(; k1<=k2; k1++) {
        ans+=sum(k1);//sum在修改时已经维护过了，不用再加tag 
    }
    return ans;
}
int qmax(int l,int r) {//忽略 
    int ans=-0x7fffffff,k1,k2;
    for(int i=l;; i++) {
        k1=i;
