    while('0'<=ch&&ch<='9') {
        x=x*10+ch-'0';
        ch=getchar();
    }
    return x*f;
}
inline void print(ll k){
	if(k<0) k=-k,putchar('-');
	if(k>9) print(k/10);
	putchar(k%10+48);
}
int seg[maxx],n,m,bit,a,b,w;
inline void setbit(){
	for(bit=1;bit<=n+1;bit<<=1);
}
inline void build(){
	for(int i=bit-1;i;--i) seg[i]=seg[i<<1]+seg[i<<1|1]; 
}
inline void update(int x,int y){
	for(seg[x+=bit]=y,x>>=1;x;x>>=1) seg[x]=seg[x<<1]+seg[x<<1|1];
}
inline int query(int s,int t){
    int ans=0;
    for(s+=bit-1,t+=bit+1;s^t^1;s>>=1,t>>=1){
        if(~s&1) ans+=seg[s^1];
        if(t&1)	ans+=seg[t^1];
    }
    return ans;
}
int xi,bi,ans;
int main(){
	n=read();
	w=read();
	setbit();
	for(int i=1;i<=n;++i){
		xi=read();
		bi=read();
		seg[xi+bit]+=bi;
	}
	build();
	int ansq;
	for(int i=1;i<=maxn-w+1;++i){
		ansq=query(i,i+w-1);
		ans=max(ans,ansq);
	}
	print(ans),putchar('\n');
	return 0;
}
