```cpp
#include<cstdio>
#include<algorithm>
#include<iostream>
#include<cstring>
using namespace std;
const int N = 100010;
long long n,m,a[N],add[N*4],w[N],ans=0,res;
struct node{   
    int l,r;
    long long sum;
}tree[N*4];
void build(int now,int l,int r){
    tree[now].l=l; tree[now].r=r;
    if(l==r){
        tree[now].sum=a[l]; return ;
    }
    int mid=(l+r)>>1;
    build(now<<1,l,mid);
    build(now<<1|1,mid+1,r);
    tree[now].sum=tree[now<<1].sum+tree[now<<1|1].sum;
}
void pushdown(int now){
    if(add[now]){
        add[now<<1]+=add[now];
        add[now<<1|1]+=add[now];
        tree[now<<1].sum+=(tree[now<<1].r-tree[now<<1].l+1)*add[now];
        tree[now<<1|1].sum+=(tree[now<<1|1].r-tree[now<<1|1].l+1)*add[now];
        add[now]=0;
    }
    return ;
}
void query(int now,int l,int r){
    int L=tree[now].l,R=tree[now].r;
    if(L>r||R<l) return ;
    if(L>=l&&R<=r){
        ans+=tree[now].sum; return ;
    }
    if(add[now]) pushdown(now);
    query(now<<1,l,r);
    query(now<<1|1,l,r);
    return ;
}
int main(){
	int x,y,z,k;
    cin>>n>>m;
    for(int i=1;i<=n;i++){
      cin>>x>>y; a[x]+=y;
    } 
    build(1,1,n);
    for(int i=1;i<=n-m+1;i++){
		ans=0;
		query(1,i,i+m-1);
		res=max(res,ans);
    }
    cout<<res<<endl;
    return 0;
}
