#include<bits/stdc++.h>
#define ls p<<1
#define rs p<<1|1
using namespace std;
inline int read(){
	int k=0;bool f=1;char ch=getchar();
	while(!isdigit(ch)){if(ch=='-')f=0;ch=getchar();}
	while(isdigit(ch)){k=(k<<1)+(k<<3)+(ch^48);ch=getchar();}	
	return f?k:-k;
}
int m,f[1000][600];
void dfs(int p){//读入部分
	int t=read(),n=read();
	if(!n){
		dfs(ls);
		dfs(rs);//①
		for(int v=m-t*2;v>0;v--)
		    for(int j=v;j>=0;j--)
		    	f[p][v+t*2]=max(f[p][v+t*2],f[ls][j]+f[rs][v-j]);//②千万不要忘了给走廊时间乘二
	}
	else {
	    for(int i=1;i<=n;i++)
	    {
	    	int w=read(),c=read();
	    	for(int v=m;v>=c+t*2;v--)
	    	    f[p][v]=max(f[p][v],f[p][v-c]+w);//③
		}
	}
}
int main(){
	m=read()-1; dfs(1);
	printf("%d",f[1][m]);
	return 0;
}
```
