#include<stdio.h>
#include<bits/stdc++.h>
#define inf 0x7f7f7f7f
using namespace std;
const int MOD=1000000007;
typedef long long ll;
typedef unsigned long long ull;
using std::max;
const int N=1000+15;
ll n,tot=1;
ll dp[N][N],w[N],c[N];
inline ll read()
{
    char ch=getchar();
    ll s=0,f=1;
    while (ch<'0'||ch>'9') {if (ch=='-') f=-1;ch=getchar();}
    while (ch>='0'&&ch<='9') {s=(s<<3)+(s<<1)+ch-'0';ch=getchar();}
    return s*f;
}
void dfs(int now)
{
    ll t=read(),x=read();
    t<<=1;
    if (x)
    {
        for (ll i=1;i<=x;i++) w[i]=read(),c[i]=read();
        for (ll i=1;i<=x;i++)
        {
            for (ll j=n;j>=c[i];j--) if (j-c[i]>=t) dp[now][j]=max(dp[now][j],dp[now][j-c[i]]+w[i]);        
        }
        return; 
    }
    ll l=++tot;dfs(tot);
    ll r=++tot;dfs(tot);
    for (ll i=t;i<=n;i++)
        for (ll j=0;j<=i-t;j++)
        {
            dp[now][i]=max(dp[now][i],dp[l][j]+dp[r][i-t-j]);
        }
}
int main()
{
    n=read();n--;
    dfs(1);
    printf("%lld\n",dp[1][n]);
    return 0;
}

```

