    int main()
    {
    //	freopen(".in","r",stdin);
    //	freopen(".out","w",stdout);
    	ios::sync_with_stdio(false);//关闭同步使cin，cout变快
    	cin>>n>>m;
    	for(int i=1;i<=m;i++)
    	{
    		cin>>z[i]>>x[i]>>y[i];
    		f[i]=i;
    	}
    	for(int i=1;i<=m;i++)
    	{
    		if(z[i]==1)
    		{
    			work1(x[i],y[i]);
