{
	if(x == f[x])
		return x ;
	return f[x] = find(f[x]) ;
}
int main()
{
	int n , m ;
	cin >> n >> m ;
	for(int i = 1 ; i <= n ; ++i)//初始化一个数所在的集合是自己 
