//代码是根据日本人挑战程序竞赛得到的
#include<iostream>
using namespace std;
const int MAXN=10000+2;
int par[MAXN],rank[MAXN];
int n,m;
//初始化n个元素
void init(int n){
    for(int i=0;i<n;i++){
        par[i]=i;
        rank[i]=0;
    }
}
//查询树的根
int find(int x){
    if(par[x]==x) return x;
    else return par[x]=find(par[x]);
}
//合并x和y所属的集合
void unite(int x,int y){
    x=find(x);
    y=find(y);
    if(x==y) return;
    if(rank[x]<rank[y]) par[x]=y;//如果两棵树深度不同，则将较短的并到较长的上
    else{
        //如果深度相同则不用处理，反之则需要进行合并
        par[y]=x;
        if(rank[y]==rank[x]) rank[x]++;
    }
}
int main(){
    int a,b,c;
    cin>>n>>m;
    for(int i=0;i<=n;i++) init(i)//进行预处理;
    for(int i=0;i<m;i++){
        cin>>a>>b>>c;
        //开始合并和查找
        if(a==1) unite(b,c);
        else{
            if(find(b)==find(c)) cout<<"Y"<<endl;
            else cout<<"N"<<endl;
        }
    }
    return 0;
}
