struct set
{
    int st[10001];//i的上级是谁
    set(){for(int i=1;i<=10000;i++)st[i]=i;}
    int find(int x)//找x的领袖（状态压缩）
    {  
        if(x==st[x])return x;
        else return st[x]=find(st[x]);
    }
    void join(int x,int y)//把x和y的团体结合
    {
        int p1=find(x),p2=find(y);
        if(p1!=p2)st[p1]=p2;
    }
    bool same(int x,int y)//x和y在不在一个团体里
    {
        int p1=find(x),p2=find(y);
        if(p1==p2)return 1;
        return 0;
