struct UFS{
    int *parent;
    int n;
    UFS(int n){
        this->n=n;
        parent=new int[n];
        for(int i=0;i<n;i++){
            parent[i]=i;
        }
    }
    ~UFS(){
        delete [] parent;
    }
    int P(int a){
        if(parent[a]!=a)
            parent[a]=P(parent[a]);
        return parent[a];
    }
    bool U(int a,int b){
        parent[P(a)]=P(b);
        return true;
    }
    bool F(int a,int b){
        return P(a)==P(b);
    }
};
