#include<cstdio>
#include<iostream>
using namespace std;
int n,m;
int father[10001];
int find_(int x)
{
    int root = x;
    while(father[root] != 0)
        root = father[root];        
    while(root != x)
    {
        int temp = father[x];
        father[x] = root;
        x = temp;
    }
    return root;
}
int main()
{
    //freopen("a.txt","r",stdin);
